import dayjs from "dayjs";
import countries_short_names from "../public/Json/countries_short_names.json";
import { Image } from "antd";
import crypto from "crypto-js";

var key = "M@RKma#Zl@H98/zMR_9";
var iv = key.slice(0, 16);
key = crypto.enc.Utf8.parse(key);
iv = crypto.enc.Utf8.parse(iv);

export const encrypt = (stringValue) => {
  try {
    if (!isValid(stringValue)) {
      location.href = "/";
    }
    var encrypted = crypto.AES.encrypt(stringValue, key, { iv: iv });
    return encrypted.toString();
  } catch (error) {
    location.href = "/";
  }
};
export const decrypt = (encryptValue) => {
  try {
    if (!isValid(encryptValue)) {
      location.href = "/";
    }
    var decrypted = crypto.AES.decrypt(encryptValue, key, { iv: iv });
    return decrypted.toString(crypto.enc.Utf8);
  } catch (error) {
    location.href = "/";
  }
};

export const convertQueryString = (obj) => {
  const generalObject = {
    username: "marknealapiusr",
    token: "b0186d19ba7e0fbab4fd6f171d236756",
  };
  return new URLSearchParams({ ...obj, ...generalObject }).toString();
};

export const convertToEST = (date) =>
  dayjs(date).tz(dayjs.tz.guess()).format("HH:mm");

const isValid = (value) => {
  if (value != undefined && value != null && value != "") {
    return true;
  }
  return false;
};
export const leaguesLogoImg = (continent, countryName, leagueName) => {
  if (continent == "" || continent == undefined) {
    let cnt = countries_short_names.filter(
      (d) => d.name.toLocaleLowerCase() == countryName.toLocaleLowerCase()
    );
    if (cnt.length > 0) {
      continent = cnt[0].continent;
    }
  }
  if (isValid(continent) && isValid(countryName) && isValid(leagueName)) {
    return (
      <Image.PreviewGroup>
        <Image
          src={`/league_logos/${continent.toLocaleLowerCase()}_${countryName.toLocaleLowerCase()}_${leagueName.toLocaleLowerCase()}.png`}
          fallback="data:image/png;base64,"
        />
      </Image.PreviewGroup>
    );
  }
  return <img src="/img/icon_11.png" alt="logo" />;
};
export const teamLogoImg = (teamName, preview = true) => {
  if (isValid(teamName)) {
    return (
      <Image
        preview={preview}
        src={`/team_logos/${teamName.toLocaleLowerCase()}.png`}
        fallback={`data:image/png;base64,`}
      />
    );
  }
  return <img src="/img/icon_11.png" alt="logo" />;
};
export const isValidValue = (value) => {
  if (value != undefined && value != null && value != "") {
    return true;
  }
  return false;
};
